// // Grid system // --------------------------------------------------
// Set the container width, and override it for fixed navbars in media queries .container { .container-fixed();
@media (min-width: @screen-sm) { width: @container-sm; } @media (min-width: @screen-md) { width: @container-md; } @media (min-width: @screen-lg-min) { width: @container-lg; } }
// mobile first defaults .row { .make-row(); }
// Common styles for small and large grid columns .make-grid-columns();
// Extra small grid // // Columns, offsets, pushes, and pulls for extra small devices like // smartphones.
.make-grid-columns-float(xs); .make-grid(@grid-columns, xs, width); .make-grid(@grid-columns, xs, pull); .make-grid(@grid-columns, xs, push); .make-grid(@grid-columns, xs, offset);
// Small grid // // Columns, offsets, pushes, and pulls for the small device range, from phones // to tablets.
@media (min-width: @screen-sm-min) { .make-grid-columns-float(sm); .make-grid(@grid-columns, sm, width); .make-grid(@grid-columns, sm, pull); .make-grid(@grid-columns, sm, push); .make-grid(@grid-columns, sm, offset); }
// Medium grid // // Columns, offsets, pushes, and pulls for the desktop device range.
@media (min-width: @screen-md-min) { .make-grid-columns-float(md); .make-grid(@grid-columns, md, width); .make-grid(@grid-columns, md, pull); .make-grid(@grid-columns, md, push); .make-grid(@grid-columns, md, offset); }
// Large grid // // Columns, offsets, pushes, and pulls for the large desktop device range.
@media (min-width: @screen-lg-min) { .make-grid-columns-float(lg); .make-grid(@grid-columns, lg, width); .make-grid(@grid-columns, lg, pull); .make-grid(@grid-columns, lg, push); .make-grid(@grid-columns, lg, offset); }
// // Tables // --------------------------------------------------
table { max-width: 100%; background-color: @table-bg; } th { text-align: left; }
// Baseline styles
.table { width: 100%; margin-bottom: @line-height-computed; // Cells > thead, > tbody, > tfoot { > tr { > th, > td { padding: @table-cell-padding; line-height: @line-height-base; vertical-align: top; border-top: 1px solid @table-border-color; } } } // Bottom align for column headings > thead > tr > th { vertical-align: bottom; border-bottom: 2px solid @table-border-color; } // Remove top border from thead by default > caption + thead, > colgroup + thead, > thead:first-child { > tr:first-child { > th, > td { border-top: 0; } } } // Account for multiple tbody instances > tbody + tbody { border-top: 2px solid @table-border-color; }
// Nesting .table { background-color: @body-bg; } }
// Condensed table w/ half padding
.table-condensed { > thead, > tbody, > tfoot { > tr { > th, > td { padding: @table-condensed-cell-padding; } } } }
// Bordered version // // Add borders all around the table and between all the columns.
.table-bordered { border: 1px solid @table-border-color; > thead, > tbody, > tfoot { > tr { > th, > td { border: 1px solid @table-border-color; } } } > thead > tr { > th, > td { border-bottom-width: 2px; } } }
// Zebra-striping // // Default zebra-stripe styles (alternating gray and transparent backgrounds)
.table-striped { > tbody > tr:nth-child(odd) { > td, > th { background-color: @table-bg-accent; } } }
// Hover effect // // Placed here since it has to come after the potential zebra striping
.table-hover { > tbody > tr:hover { > td, > th { background-color: @table-bg-hover; } } }
// Table cell sizing // // Reset default table behavior
table col[class*="col-"] { position: static; // Prevent border hiding in Firefox and IE9/10 (see https://github.com/twbs/bootstrap/issues/11623) float: none; display: table-column; } table { td, th { &[class*="col-"] { float: none; display: table-cell; } } }
// Table backgrounds // // Exact selectors below required to override `.table-striped` and prevent // inheritance to nested tables.
// Generate the contextual variants .table-row-variant(active; @table-bg-active); .table-row-variant(success; @state-success-bg); .table-row-variant(danger; @state-danger-bg); .table-row-variant(warning; @state-warning-bg);
// Responsive tables // // Wrap your tables in `.table-responsive` and we'll make them mobile friendly // by enabling horizontal scrolling. Only applies <768px. Everything above that // will display normally.
@media (max-width: @screen-xs-max) { .table-responsive { width: 100%; margin-bottom: (@line-height-computed * 0.75); overflow-y: hidden; overflow-x: scroll; -ms-overflow-style: -ms-autohiding-scrollbar; border: 1px solid @table-border-color; -webkit-overflow-scrolling: touch;
// Tighten up spacing > .table { margin-bottom: 0;
// Ensure the content doesn't wrap > thead, > tbody, > tfoot { > tr { > th, > td { white-space: nowrap; } } } }
// Special overrides for the bordered tables > .table-bordered { border: 0;
// Nuke the appropriate borders so that the parent can handle them > thead, > tbody, > tfoot { > tr { > th:first-child, > td:first-child { border-left: 0; } > th:last-child, > td:last-child { border-right: 0; } } }
// Only nuke the last row's bottom-border in `tbody` and `tfoot` since // chances are there will be only one `tr` in a `thead` and that would // remove the border altogether. > tbody, > tfoot { > tr:last-child { > th, > td { border-bottom: 0; } } }
} } }
// // Forms // --------------------------------------------------
// Normalize non-controls // // Restyle and baseline non-control form elements.
fieldset { padding: 0; margin: 0; border: 0; }
legend { display: block; width: 100%; padding: 0; margin-bottom: @line-height-computed; font-size: (@font-size-base * 1.5); line-height: inherit; color: @legend-color; border: 0; border-bottom: 1px solid @legend-border-color; }
label { display: inline-block; margin-bottom: 5px; font-weight: bold; }
// Normalize form controls
// Override content-box in Normalize (* isn't specific enough) input[type="search"] { .box-sizing(border-box); }
// Position radios and checkboxes better input[type="radio"], input[type="checkbox"] { margin: 4px 0 0; margin-top: 1px \9; /* IE8-9 */ line-height: normal; }
// Set the height of select and file controls to match text inputs input[type="file"] { display: block; }
// Make multiple select elements height not fixed select[multiple], select[size] { height: auto; }
// Fix optgroup Firefox bug per https://github.com/twbs/bootstrap/issues/7611 select optgroup { font-size: inherit; font-style: inherit; font-family: inherit; }
// Focus for select, file, radio, and checkbox input[type="file"]:focus, input[type="radio"]:focus, input[type="checkbox"]:focus { .tab-focus(); }
// Fix for Chrome number input // Setting certain font-sizes causes the `I` bar to appear on hover of the bottom increment button. // See https://github.com/twbs/bootstrap/issues/8350 for more. input[type="number"] { &::-webkit-outer-spin-button, &::-webkit-inner-spin-button { height: auto; } }
// Adjust output element output { display: block; padding-top: (@padding-base-vertical + 1); font-size: @font-size-base; line-height: @line-height-base; color: @input-color; vertical-align: middle; }
// Common form controls // // Shared size and type resets for form controls. Apply `.form-control` to any // of the following form controls: // // select // textarea // input[type="text"] // input[type="password"] // input[type="datetime"] // input[type="datetime-local"] // input[type="date"] // input[type="month"] // input[type="time"] // input[type="week"] // input[type="number"] // input[type="email"] // input[type="url"] // input[type="search"] // input[type="tel"] // input[type="color"]
.form-control { display: block; width: 100%; height: @input-height-base; // Make inputs at least the height of their button counterpart (base line-height + padding + border) padding: @padding-base-vertical @padding-base-horizontal; font-size: @font-size-base; line-height: @line-height-base; color: @input-color; vertical-align: middle; background-color: @input-bg; background-image: none; // Reset unusual Firefox-on-Android default style; see https://github.com/necolas/normalize.css/issues/214 border: 1px solid @input-border; border-radius: @input-border-radius; .box-shadow(inset 0 1px 1px rgba(0,0,0,.075)); .transition(~"border-color ease-in-out .15s, box-shadow ease-in-out .15s");
// Customize the `:focus` state to imitate native WebKit styles. .form-control-focus();
// Placeholder // // Placeholder text gets special styles because when browsers invalidate entire // lines if it doesn't understand a selector/ .placeholder();
// Disabled and read-only inputs // Note: HTML5 says that controls under a fieldset > legend:first-child won't // be disabled if the fieldset is disabled. Due to implementation difficulty, // we don't honor that edge case; we style them as disabled anyway. &[disabled], &[readonly], fieldset[disabled] & { cursor: not-allowed; background-color: @input-bg-disabled; }
// Reset height for `textarea`s textarea& { height: auto; } }
// Form groups // // Designed to help with the organization and spacing of vertical forms. For // horizontal forms, use the predefined grid classes.
.form-group { margin-bottom: 15px; }
// Checkboxes and radios // // Indent the labels to position radios/checkboxes as hanging controls.
.radio, .checkbox { display: block; min-height: @line-height-computed; // clear the floating input if there is no label text margin-top: 10px; margin-bottom: 10px; padding-left: 20px; vertical-align: middle; label { display: inline; margin-bottom: 0; font-weight: normal; cursor: pointer; } } .radio input[type="radio"], .radio-inline input[type="radio"], .checkbox input[type="checkbox"], .checkbox-inline input[type="checkbox"] { float: left; margin-left: -20px; } .radio + .radio, .checkbox + .checkbox { margin-top: -5px; // Move up sibling radios or checkboxes for tighter spacing }
// Radios and checkboxes on same line .radio-inline, .checkbox-inline { display: inline-block; padding-left: 20px; margin-bottom: 0; vertical-align: middle; font-weight: normal; cursor: pointer; } .radio-inline + .radio-inline, .checkbox-inline + .checkbox-inline { margin-top: 0; margin-left: 10px; // space out consecutive inline controls }
// Apply same disabled cursor tweak as for inputs // // Note: Neither radios nor checkboxes can be readonly. input[type="radio"], input[type="checkbox"], .radio, .radio-inline, .checkbox, .checkbox-inline { &[disabled], fieldset[disabled] & { cursor: not-allowed; } }
// Form control sizing .input-sm { .input-size(@input-height-small; @padding-small-vertical; @padding-small-horizontal; @font-size-small; @line-height-small; @border-radius-small); }
.input-lg { .input-size(@input-height-large; @padding-large-vertical; @padding-large-horizontal; @font-size-large; @line-height-large; @border-radius-large); }
// Form control feedback states // // Apply contextual and semantic states to individual form controls.
// Warning .has-warning { .form-control-validation(@state-warning-text; @state-warning-text; @state-warning-bg); } // Error .has-error { .form-control-validation(@state-danger-text; @state-danger-text; @state-danger-bg); } // Success .has-success { .form-control-validation(@state-success-text; @state-success-text; @state-success-bg); }
// Static form control text // // Apply class to a `p` element to make any string of text align with labels in // a horizontal form layout.
.form-control-static { margin-bottom: 0; // Remove default margin from `p` }
// Help text // // Apply to any element you wish to create light text for placement immediately // below a form control. Use for general help, formatting, or instructional text.
.help-block { display: block; // account for any element using help-block margin-top: 5px; margin-bottom: 10px; color: lighten(@text-color, 25%); // lighten the text some for contrast }
// Inline forms // // Make forms appear inline(-block) by adding the `.form-inline` class. Inline // forms begin stacked on extra small (mobile) devices and then go inline when // viewports reach <768px. // // Requires wrapping inputs and labels with `.form-group` for proper display of // default HTML form controls and our custom form controls (e.g., input groups). // // Heads up! This is mixin-ed into `.navbar-form` in navbars.less.
.form-inline {
// Kick in the inline @media (min-width: @screen-sm) { // Inline-block all the things for "inline" .form-group { display: inline-block; margin-bottom: 0; vertical-align: middle; }
// In navbar-form, allow folks to *not* use `.form-group` .form-control { display: inline-block; }
// Override `width: 100%;` when not within a `.form-group` select.form-control { width: auto; }
// Remove default margin on radios/checkboxes that were used for stacking, and // then undo the floating of radios and checkboxes to match (which also avoids // a bug in WebKit: https://github.com/twbs/bootstrap/issues/1969). .radio, .checkbox { display: inline-block; margin-top: 0; margin-bottom: 0; padding-left: 0; } .radio input[type="radio"], .checkbox input[type="checkbox"] { float: none; margin-left: 0; } } }
// Horizontal forms // // Horizontal forms are built on grid classes and allow you to create forms with // labels on the left and inputs on the right.
.form-horizontal {
// Consistent vertical alignment of labels, radios, and checkboxes .control-label, .radio, .checkbox, .radio-inline, .checkbox-inline { margin-top: 0; margin-bottom: 0; padding-top: (@padding-base-vertical + 1); // Default padding plus a border } // Account for padding we're adding to ensure the alignment and of help text // and other content below items .radio, .checkbox { min-height: @line-height-computed + (@padding-base-vertical + 1); }
// Make form groups behave like rows .form-group { .make-row(); }
.form-control-static { padding-top: (@padding-base-vertical + 1); }
// Only right align form labels here when the columns stop stacking @media (min-width: @screen-sm-min) { .control-label { text-align: right; } } }
// // Buttons // --------------------------------------------------
// Base styles // --------------------------------------------------
.btn { display: inline-block; margin-bottom: 0; // For input.btn font-weight: @btn-font-weight; text-align: center; vertical-align: middle; cursor: pointer; background-image: none; // Reset unusual Firefox-on-Android default style; see https://github.com/necolas/normalize.css/issues/214 border: 1px solid transparent; white-space: nowrap; .button-size(@padding-base-vertical; @padding-base-horizontal; @font-size-base; @line-height-base; @border-radius-base); .user-select(none);
&:focus { .tab-focus(); }
&:hover, &:focus { color: @btn-default-color; text-decoration: none; }
&:active, &.active { outline: 0; background-image: none; .box-shadow(inset 0 3px 5px rgba(0,0,0,.125)); }
&.disabled, &[disabled], fieldset[disabled] & { cursor: not-allowed; pointer-events: none; // Future-proof disabling of clicks .opacity(.65); .box-shadow(none); } }
// Alternate buttons // --------------------------------------------------
.btn-default { .button-variant(@btn-default-color; @btn-default-bg; @btn-default-border); } .btn-primary { .button-variant(@btn-primary-color; @btn-primary-bg; @btn-primary-border); } // Warning appears as orange .btn-warning { .button-variant(@btn-warning-color; @btn-warning-bg; @btn-warning-border); } // Danger and error appear as red .btn-danger { .button-variant(@btn-danger-color; @btn-danger-bg; @btn-danger-border); } // Success appears as green .btn-success { .button-variant(@btn-success-color; @btn-success-bg; @btn-success-border); } // Info appears as blue-green .btn-info { .button-variant(@btn-info-color; @btn-info-bg; @btn-info-border); }
// Link buttons // -------------------------
// Make a button look and behave like a link .btn-link { color: @link-color; font-weight: normal; cursor: pointer; border-radius: 0;
&, &:active, &[disabled], fieldset[disabled] & { background-color: transparent; .box-shadow(none); } &, &:hover, &:focus, &:active { border-color: transparent; } &:hover, &:focus { color: @link-hover-color; text-decoration: underline; background-color: transparent; } &[disabled], fieldset[disabled] & { &:hover, &:focus { color: @btn-link-disabled-color; text-decoration: none; } } }
// Button Sizes // --------------------------------------------------
.btn-lg { // line-height: ensure even-numbered height of button next to large input .button-size(@padding-large-vertical; @padding-large-horizontal; @font-size-large; @line-height-large; @border-radius-large); } .btn-sm { // line-height: ensure proper height of button next to small input .button-size(@padding-small-vertical; @padding-small-horizontal; @font-size-small; @line-height-small; @border-radius-small); } .btn-xs { .button-size(@padding-xs-vertical; @padding-xs-horizontal; @font-size-small; @line-height-small; @border-radius-small); }
// Block button // --------------------------------------------------
.btn-block { display: block; width: 100%; padding-left: 0; padding-right: 0; }
// Vertically space out multiple block buttons .btn-block + .btn-block { margin-top: 5px; }
// Specificity overrides input[type="submit"], input[type="reset"], input[type="button"] { &.btn-block { width: 100%; } }
