// Bar of progress .progress-bar { float: left; width: 0%; height: 100%; font-size: @font-size-small; line-height: @line-height-computed; color: @progress-bar-color; text-align: center; background-color: @progress-bar-bg; .box-shadow(inset 0 -1px 0 rgba(0,0,0,.15)); .transition(width .6s ease); }
// Striped bars .progress-striped .progress-bar { #gradient > .striped(); background-size: 40px 40px; }
// Call animation for the active one .progress.active .progress-bar { .animation(progress-bar-stripes 2s linear infinite); }
// Variations // -------------------------
.progress-bar-success { .progress-bar-variant(@progress-bar-success-bg); }
.progress-bar-info { .progress-bar-variant(@progress-bar-info-bg); }
.progress-bar-warning { .progress-bar-variant(@progress-bar-warning-bg); }
.progress-bar-danger { .progress-bar-variant(@progress-bar-danger-bg); }
// Media objects // Source: http://stubbornella.org/content/?p=497 // --------------------------------------------------
// Common styles // -------------------------
// Clear the floats .media, .media-body { overflow: hidden; zoom: 1; }
// Proper spacing between instances of .media .media, .media .media { margin-top: 15px; } .media:first-child { margin-top: 0; }
// For images and videos, set to block .media-object { display: block; }
// Reset margins on headings for tighter default spacing .media-heading { margin: 0 0 5px; }
// Media image alignment // -------------------------
.media { > .pull-left { margin-right: 10px; } > .pull-right { margin-left: 10px; } }
// Media list variation // -------------------------
// Undo default ul/ol styles .media-list { padding-left: 0; list-style: none; }
// // List groups // --------------------------------------------------
// Base class // // Easily usable on <ul>, <ol>, or <div>. .list-group { // No need to set list-style: none; since .list-group-item is block level margin-bottom: 20px; padding-left: 0; // reset padding because ul and ol }
// Individual list items // -------------------------
.list-group-item { position: relative; display: block; padding: 10px 15px; // Place the border on the list items and negative margin up for better styling margin-bottom: -1px; background-color: @list-group-bg; border: 1px solid @list-group-border;
// Round the first and last items &:first-child { .border-top-radius(@list-group-border-radius); } &:last-child { margin-bottom: 0; .border-bottom-radius(@list-group-border-radius); }
// Align badges within list items > .badge { float: right; } > .badge + .badge { margin-right: 5px; } }
// Linked list items a.list-group-item { color: @list-group-link-color;
.list-group-item-heading { color: @list-group-link-heading-color; }
// Hover state &:hover, &:focus { text-decoration: none; background-color: @list-group-hover-bg; }
// Active class on item itself, not parent &.active, &.active:hover, &.active:focus { z-index: 2; // Place active items above their siblings for proper border styling color: @list-group-active-color; background-color: @list-group-active-bg; border-color: @list-group-active-border;
// Force color to inherit for custom content .list-group-item-heading { color: inherit; } .list-group-item-text { color: lighten(@list-group-active-bg, 40%); } } }
// Custom content options // -------------------------
.list-group-item-heading { margin-top: 0; margin-bottom: 5px; } .list-group-item-text { margin-bottom: 0; line-height: 1.3; }
// // Panels // --------------------------------------------------
// Base class .panel { margin-bottom: @line-height-computed; background-color: @panel-bg; border: 1px solid transparent; border-radius: @panel-border-radius; .box-shadow(0 1px 1px rgba(0,0,0,.05)); }
// Panel contents .panel-body { padding: 15px; .clearfix(); }
// List groups in panels // // By default, space out list group content from panel headings to account for // any kind of custom content between the two.
.panel { > .list-group { margin-bottom: 0;
.list-group-item { border-width: 1px 0;
// Remove border radius for top one &:first-child { .border-top-radius(0); } // But keep it for the last one &:last-child { border-bottom: 0; } } } } // Collapse space between when there's no additional content. .panel-heading + .list-group { .list-group-item:first-child { border-top-width: 0; } }
// Tables in panels // // Place a non-bordered `.table` within a panel (not within a `.panel-body`) and // watch it go full width.
.panel { > .table, > .table-responsive > .table { margin-bottom: 0; } > .panel-body + .table, > .panel-body + .table-responsive { border-top: 1px solid @table-border-color; } > .table > tbody:first-child th, > .table > tbody:first-child td { border-top: 0; } > .table-bordered, > .table-responsive > .table-bordered { border: 0; > thead, > tbody, > tfoot { > tr { > th:first-child, > td:first-child { border-left: 0; } > th:last-child, > td:last-child { border-right: 0; }
&:last-child > th, &:last-child > td { border-bottom: 0; } } } } > .table-responsive { border: 0; margin-bottom: 0; } }
// Optional heading .panel-heading { padding: 10px 15px; border-bottom: 1px solid transparent; .border-top-radius(@panel-border-radius - 1);
> .dropdown .dropdown-toggle { color: inherit; } }
// Within heading, strip any `h*` tag of it's default margins for spacing. .panel-title { margin-top: 0; margin-bottom: 0; font-size: ceil((@font-size-base * 1.125)); color: inherit;
> a { color: inherit; } }
// Optional footer (stays gray in every modifier class) .panel-footer { padding: 10px 15px; background-color: @panel-footer-bg; border-top: 1px solid @panel-inner-border; .border-bottom-radius(@panel-border-radius - 1); }
// Collapsable panels (aka, accordion) // // Wrap a series of panels in `.panel-group` to turn them into an accordion with // the help of our collapse JavaScript plugin.
.panel-group { // Tighten up margin so it's only between panels .panel { margin-bottom: 0; border-radius: @panel-border-radius; overflow: hidden; // crop contents when collapsed + .panel { margin-top: 5px; } }
.panel-heading { border-bottom: 0; + .panel-collapse .panel-body { border-top: 1px solid @panel-inner-border; } } .panel-footer { border-top: 0; + .panel-collapse .panel-body { border-bottom: 1px solid @panel-inner-border; } } }
// Contextual variations .panel-default { .panel-variant(@panel-default-border; @panel-default-text; @panel-default-heading-bg; @panel-default-border); } .panel-primary { .panel-variant(@panel-primary-border; @panel-primary-text; @panel-primary-heading-bg; @panel-primary-border); } .panel-success { .panel-variant(@panel-success-border; @panel-success-text; @panel-success-heading-bg; @panel-success-border); } .panel-warning { .panel-variant(@panel-warning-border; @panel-warning-text; @panel-warning-heading-bg; @panel-warning-border); } .panel-danger { .panel-variant(@panel-danger-border; @panel-danger-text; @panel-danger-heading-bg; @panel-danger-border); } .panel-info { .panel-variant(@panel-info-border; @panel-info-text; @panel-info-heading-bg; @panel-info-border); }
