Service.prototype.getDs = function(obj) { var params = obj.getParams(); var path = params.getRef("path"); obj.set(".", this.ds.getDs(path)); }; /** * @param {pebble.Pebble} obj * @private */ Service.prototype.generateIndexes = function(obj) {
