Service.prototype.getCollections = function(ds, paths) { this.ds = ds; this.collections = []; this.count = paths.length; for (var i = 0; i < paths.length; i++) { var path = paths[i].getRef("."); if (path.indexOf("*") == -1) { this.collections.push(path); this.count -= 1; } else { this.handleWild(path); } } if (this.count == 0) { this.buildIndexes(); } }; Service.prototype.handleWild = function(path) { var wildIndex = path.indexOf("*"); if (wildIndex != -1) { var rem = path.substring(wildIndex + 1); var colPath = path.substring(0, wildIndex - 1); var query = new pebble.Pebble(); query.setRef("from.path", colPath); var recs = this.ds.doQuery(query, null, null); if (rem.indexOf("*") == -1) { for (var i = 0; i < recs.length; i++) { var rec = recs[i]; this.collections.push(colPath + "." + rec.getTagName() + rem); } } else { this.count += recs.length; for (var i = 0; i < recs.length; i++) { var rec = new pebble.Pebble(recs[i]); this.handleWild(colPath + "." + rec.getTagName() + rem); } } this.count -= 1; //for parent
} };
Service.prototype.buildIndexes = function() { //build indexes var count = 0; var colCount = this.collections.length; this.ds.deleteCollections(['theModel.clientWorkspace.globalIndexItems']); for (var i = 0; i < this.collections.length; i++) { //theModel.libs... var collection = this.collections[i]; var elements = collection.split("."); var lib = elements[3]; var collectionName = elements[4]; var query = new pebble.Pebble(); query.setRef("from.path", collection); var recs = this.ds.doQuery(query, null, null); for (var j = 0; j < recs.length; j++) { var item = recs[j]; var data = new pebble.Pebble(); data.setValue("text", item.getTagName()); data.setRef("collection", collection); data.setRef("dataPath", collection.replace("theModel.", "model.") + "." + item.getTagName()); this.ds.create("theModel.clientWorkspace.globalIndexItems", data, null, null); } this.colCount -= 1; //for parent if (this.colCount == 0) { //ready } } };
