pebble.inherits(pebble.libs.backboneBuilder.Analyzer, pebble.libs.standard.ArrayTreeBase);
/** * @override */ pebble.libs.backboneBuilder.Analyzer.prototype.onReady = function () {
var pxml = this.getOriginInfo("model").getCreateOnNull();
this.setLoaded();
}; pebble.libs.backboneBuilder.Analyzer.prototype.executeMethod = function(funcName, obj) { var ret = 1; //list all ui public methods if (funcName == "searchUsage") { this.searchUsage(obj); } else if (funcName == "searchHasValue") { this.searchWithCriteria(obj, checkHasValue); } else if (funcName == "searchHasReference") { this.searchWithCriteria(obj, checkHasReference); } else if (funcName == "findUnused") { this.findUnused(obj); } else if (funcName == "search") { this.searchWithCriteria(obj, searchDocByKeyword); } else { return pebble.libs.backboneBuilder.Analyzer.superClass_.executeMethod.call(this, funcName, obj); } return ret; }; /** * param {pebble.Pebble} item * param {pebble.Pebble} searchCriteria */ function searchDocByKeyword (item, searchCriteria) { var keyword = searchCriteria.getValue("keyword"); var s = item.toString(); return s.indexOf(keyword) != -1; } /** * param {pebble.Pebble} item * param {pebble.Pebble} searchCriteria */ function checkHasValue (item, searchCriteria) { var meta = searchCriteria.getValue("meta"); var recs = item.getRecords("."); for (var i = 0; i < recs.length; i++) { var rec = recs[i]; if (rec.getTagName() == meta && rec.getValue(".")) { return true; } else { if (checkHasValue(rec, searchCriteria)) { return true; } } } return false; } /** * param {pebble.Pebble} item * param {pebble.Pebble} searchCriteria */ function checkHasReference (item, searchCriteria) { var ref = searchCriteria.getRef("ref"); var recs = item.getRecords("."); for (var i = 0; i < recs.length; i++) { var rec = recs[i]; if (rec.getRef(".") == ref) { return true; } else { if (checkHasReference(rec, searchCriteria)) { return true; } } } return false; }
function searchDocs (workspace, searchCriteria, fn) { var workspacePeb = workspace.getDataSource(); var filteredData = new pebble.Pebble(); var searchAll = searchCriteria.getBool("searchAll"); var searchTypes = searchCriteria.getBool("searchTypes"); var searchControls = searchCriteria.getBool("searchControls"); var searchLibs = searchCriteria.getBool("searchLibs"); var tables = workspacePeb.getRecords("."); var count = 0; for (var i = 0; i < tables.length; i++) { var table = tables[i]; var tableName = table.getTagName().replace(/_/g, "."); var collectionMeta = tableName.split(".").pop(); if (searchAll || (collectionMeta == "controls" && searchControls) || (collectionMeta == "types" && searchTypes) || (collectionMeta == "libs" && searchLibs)) { var query = new pebble.Pebble(); query.setRef("from.path", tableName); var docs = workspace.doQuery(query); for (var j = 0; j < docs.length; j++) { var doc = docs[j]; if (fn(doc, searchCriteria)) { var item = new pebble.Pebble(); item.setRef("dataPath", tableName.replace("theModel", "model") + "." + doc.getTagName()); item.setValue("text", doc.getTagName()); filteredData.set("a" + count, item); count += 1; } } } } return filteredData; }
function searchUnusedDocs (workspace, searchCriteria, fn) { var workspacePeb = workspace.getDataSource(); var filteredData = new pebble.Pebble(); var searchTypes = searchCriteria.getBool("searchTypes"); var searchControls = searchCriteria.getBool("searchControls"); var searchLibs = searchCriteria.getBool("searchLibs"); var tables = workspacePeb.getRecords("."); var count = 0; for (var i = 0; i < tables.length; i++) { var table = tables[i]; var tableName = table.getTagName().replace(/_/g, "."); var collectionMeta = tableName.split(".").pop(); if ((collectionMeta == "controls" && searchControls) || (collectionMeta == "types" && searchTypes) || (collectionMeta == "libs" && searchLibs)) { var query = new pebble.Pebble(); query.setRef("from.path", tableName); var docs = workspace.doQuery(query); for (var j = 0; j < docs.length; j++) { var doc = docs[j]; var path = tableName + "." + doc.getTagName(); searchCriteria.setRef("ref", path); if (!checkUsed(workspace, searchCriteria, checkHasReference)) { var item = new pebble.Pebble(); item.setRef("dataPath", tableName.replace("theModel", "model") + "." + doc.getTagName()); item.setValue("text", doc.getTagName()); filteredData.set("a" + count, item); count += 1; } } } } return filteredData; }
/** * expects searchCriteria.ref */ function checkUsed (workspace, searchCriteria, fn) { var workspacePeb = workspace.getDataSource(); var tables = workspacePeb.getRecords("."); var count = 0; for (var i = 0; i < tables.length; i++) { var table = tables[i]; var tableName = table.getTagName().replace(/_/g, "."); var collectionMeta = tableName.split(".").pop(); var query = new pebble.Pebble(); query.setRef("from.path", tableName); var docs = workspace.doQuery(query); for (var j = 0; j < docs.length; j++) { var doc = docs[j]; if (fn(doc, searchCriteria)) { return true; } } } return false; } /** * param {pebble.Pebble} obj * param {Function} fn */ pebble.libs.backboneBuilder.Analyzer.prototype.searchWithCriteria = function (obj, fn) { var params = obj.getParams(); var searchCriteria = params.get("searchCriteria"); var that = this; pebble.libs.backboneBuilder.getDs(this, function(ws) { var workspace = new pebble.shared.ServerDataImpl_Xml(ws); var filteredData = searchDocs(workspace, searchCriteria, fn); that.getOriginInfo("filteredGlobalIndexItems").set(filteredData, that, false, obj.getCallback()); }); }; /** * param {pebble.Pebble} obj * param {Function} fn */ pebble.libs.backboneBuilder.Analyzer.prototype.findUnused = function (obj, fn) { var params = obj.getParams(); var searchCriteria = params.get("searchCriteria"); var that = this; pebble.libs.backboneBuilder.getDs(this, function(ws) { var workspace = new pebble.shared.ServerDataImpl_Xml(ws); var filteredData = searchUnusedDocs(workspace, searchCriteria, fn); that.getOriginInfo("filteredGlobalIndexItems").set(filteredData, that, false, obj.getCallback()); }); };
pebble.libs.backboneBuilder.Analyzer.prototype.checkWithFn = function () { this.log.innerHtml = ""; var workspacePeb = binder.data.workspace.getDataSource(); var fn = document.getElementById("findFnInput").value; var tables = workspacePeb.getRecords("."); for (var i = 0; i < tables.length; i++) { var table = tables[i]; var tableName = table.getTagName().replace(/_/g, "."); var query = new pebble.Pebble(); query.setRef("from.path", tableName); var docs = binder.data.workspace.doQuery(query); eval("var fn = " + fn); for (var j = 0; j < docs.length; j++) { var doc = docs[j]; this.findDocUsages(doc, fn, tableName + "." + doc.getTagName()); } } };
pebble.libs.backboneBuilder.Analyzer.prototype.checkWithFnFullDoc = function () { this.log.innerHtml = ""; var workspacePeb = binder.data.workspace.getDataSource(); var fn = document.getElementById("findFnInput").value; var tables = workspacePeb.getRecords("."); for (var i = 0; i < tables.length; i++) { var table = tables[i]; var tableName = table.getTagName().replace(/_/g, "."); var query = new pebble.Pebble(); query.setRef("from.path", tableName); var docs = binder.data.workspace.doQuery(query); eval("var fn = " + fn); for (var j = 0; j < docs.length; j++) { var doc = docs[j]; this.findDocUsagesFullDoc(doc.toString(), fn, tableName + "." + doc.getTagName()); } } };
pebble.libs.backboneBuilder.Analyzer.prototype.findDocUsages = function (item, fn, docPath) { var ref = item.getRef("."); if (ref != null && fn(ref)) { pebble.shared.logger.log("INFO", docPath.replace("theModel.", "") + "\n", function(data) {
var collection = data.substring(0, data.lastIndexOf(".")); var name = data.substring(data.lastIndexOf(".") + 1);
}, docPath); } var recs = item.getRecords("."); for (var i = 0; i < recs.length; i++) { this.findDocUsages(recs[i], fn, docPath); } };
