pebble.libs.backboneBuilder.CustomStackPanel.prototype.animatePanels = function(meta) { this.isAnimationVertical = false; //all panels are same full size var newModule = this.modules[meta]; var newEl = newModule.element;
// Calculate the direction that the new widget will enter. var slots = this.getOriginInfo("model").get(); var oldIndex; if (this.currentMeta != null) { oldIndex = slots.getIndex(".", this.currentMeta); } else { oldIndex = -1; } var newIndex = slots.getIndex(".", meta); var direction = (oldIndex < newIndex) ? 1: -1;
var newElHeight = newModule.item.getOffsetHeight(); var newElWidth = newModule.item.getOffsetWidth();
var offset = this.isAnimationVertical ? direction * newElHeight : direction * newElWidth;
var transitionProp = this.isAnimationVertical ? "top" : "left"; var transitionStyle = "-moz-transition:" + transitionProp + " 0.7s ease-out; -o-transition:" + transitionProp + " 0.7s ease-out; -webkit-transition:" + transitionProp + " 0.7s ease-out; transition:" + transitionProp + " 0.7s ease-out;";
// The new layer starts off to one side.
//turn transitions off while we set newEl.setAttribute("style", "");
var newStyle = ""; if (this.isAnimationVertical) { newStyle += "top:" + offset + "px;"; } else { newStyle += "left:" + offset + "px;"; } //set visible, turn transition on newEl.setAttribute("style", newStyle + "position:absolute;width:100%;height:100%;visibility:visible;" + transitionStyle);
// Set the end positions of the layers. if (this.currentMeta != null) { // The old layer ends off to one side. var currentEl = this.modules[this.currentMeta].element;
this.applyStyleProperties(currentEl, transitionStyle);
offset = -offset; if (this.isAnimationVertical) { this.applyStyleProperties(currentEl, "top:" + offset + "px;"); } else { this.applyStyleProperties(currentEl, "left:" + offset + "px;"); } //turn transition off delete currentEl.style["-moz-transition"]; delete currentEl.style["-o-transition"]; delete currentEl.style["-webkit-transition"]; delete currentEl.style["transition"];
}
// The new layer ends centered in the panel. // this.applyStyleProperties(newEl, transitionStyle);
if (this.isAnimationVertical) { this.applyStyleProperties(newEl, "top:0px;"); } else { this.applyStyleProperties(newEl, "left:0px;"); }
//turn transition off delete newEl.style["-moz-transition"]; delete newEl.style["-o-transition"]; delete newEl.style["-webkit-transition"]; delete newEl.style["transition"];
};
/** * @param {Element} ele * @param {string} overlayStyle */ pebble.libs.backboneBuilder.CustomStackPanel.prototype.applyStyleProperties = function(ele, overlayStyle) { var style;
var currentStyle = ele.getAttribute("style"); if (currentStyle != null) { style = pebble.libs.standard.StackPanel.getDiffStyle(currentStyle, overlayStyle);
} else { style = currentStyle; } ele.setAttribute("style", style); };
/** * @param {string} existingStyle * @param {string} overlayStyle * @return {string} */ pebble.libs.backboneBuilder.CustomStackPanel.getDiffStyle = function(existingStyle, overlayStyle) { var ret = "";
if (existingStyle != null) { var existingElements = existingStyle.split(";"); var existingMap = {};
for (var i = 0; i < existingElements.length; i++) { var prop = existingElements[i]; prop = prop.trim(); if (prop != ""){ var pair = prop.split(":"); var name = pair[0].trim(); var val = pair[1].trim(); existingMap[name] = val; } } var overlayElements = overlayStyle.split(";"); for (var i = 0; i < overlayElements.length; i++) { var prop = overlayElements[i]; prop = prop.trim(); if (prop != ""){ var pair = prop.split(":"); var name = pair[0].trim(); var val = pair[1].trim(); //overwrite if exist in existing existingMap[name] = val; } }
for (var key in existingMap) { ret += key + ":" + existingMap[key] + ";"; } } else { ret = overlayStyle; } return ret; };
(function () { function addClientScripts (clientScripts, id) { var js = ''; for (var i = 0; i < clientScripts.length; i++) { var clientScript = clientScripts[i]; js += clientScript.getValue('devCode'); } if (js != '') { addScriptCode(id, js); } } /** * Insert style tag * @param {string} id * @param {string} code */ function addScriptCode (id, code) {
var head = document.getElementsByTagName('head')[0]; var script = document.getElementById(id); if (script == null) { var ele = document.createElement('script'); ele.setAttribute('id', id); head.appendChild(ele); script = document.getElementById(id); } script.innerHTML = code; } /** * @param {pebble.Pebble} sharedLib */ function getScriptsAndCss_client(sharedLib) {
