pebble.libs.backboneBuilder.ClosureCompiler.prototype.request = function(code) { var data = ""; data += "js_code=" + encodeURIComponent(code) + "&"; //data += "code_url=http://&";
//WHITESPACE_ONLY, SIMPLE_OPTIMIZATIONS, and ADVANCED_OPTIMIZATIONS data += "&compilation_level=SIMPLE_OPTIMIZATIONS"; //data += "&compilation_level=ADVANCED_OPTIMIZATIONS";
//text, xml, json data += "&output_format=json";
//??? //data += "&output_file_name=something.js";
//compiled_code, warnings, errors, and statistics data += "&output_info=compiled_code"; data += "&output_info=statistics"; data += "&output_info=warnings"; data += "&output_info=errors";
data += "&warning_level=verbose";
var xmlhttp = new XMLHttpRequest(); xmlhttp.open('POST', 'http://closure-compiler.appspot.com/compile', true); xmlhttp.setRequestHeader("Content-type","application/x-www-form-urlencoded"); var that = this; xmlhttp.onreadystatechange = function() { if (xmlhttp.readyState == 4 && xmlhttp.status == 200){ var result = JSON.parse(xmlhttp.responseText);
if (result.compiledCode) { var compiledCode = new pebble.Pebble(); compiledCode.setMarkup(".", result.compiledCode); var compiledCodeOrigin = that.getOriginInfo("compilationResult.compiledCode"); compiledCodeOrigin.set(compiledCode, that, false); }
//statistics var stats = result.statistics; var statPeb = new pebble.Pebble(); statPeb.setValue("originalSize", stats.originalSize); statPeb.setValue("originalGZipSize", stats.originalGzipSize); statPeb.setValue("compressedSize", stats.compressedSize); statPeb.setValue("compressedGZipSize", stats.compressedGzipSize); statPeb.setValue("compileTime", stats.compileTime);
var statsOrigin = that.getOriginInfo("compilationResult.statistics"); statsOrigin.set(statPeb, that, false);
if (result.warnings) { var warnings = new pebble.Pebble(); for (var i = 0; i < result.warnings.length; i++) { var warning = result.warnings[i];
var warningPeb = new pebble.Pebble();
warningPeb.setValue("warning", warning.warning); warningPeb.setValue("type", warning.type); warningPeb.setValue("file", warning.file); warningPeb.setValue("lineno", warning.lineno); warningPeb.setValue("charno", warning.charno); warningPeb.setValue("line", warning.line);
warnings.add2Array(".", warningPeb); }
var origin = that.getOriginInfo("compilationResult.warnings"); origin.set(warnings, that, false); }
if (result.errors) { var errors = new pebble.Pebble(); for (var i = 0; i < result.errors.length; i++) { var error = result.errors[i];
var errorPeb = new pebble.Pebble();
errorPeb.setValue("error", error.error); errorPeb.setValue("type", error.type); errorPeb.setValue("file", error.file); errorPeb.setValue("lineno", error.lineno); errorPeb.setValue("charno", error.charno); errorPeb.setValue("line", error.line);
errors.add2Array(".", errorPeb); }
var errorsOrigin = that.getOriginInfo("compilationResult.errors"); errorsOrigin.set(errors, that, false); }
} }; xmlhttp.send(data); };
